import React from 'react';
import Axios from 'axios';
import Img from 'react-image';
// const Img = require('react-image');
import Logo from './posts/rsz_4.png'

export default class Upload extends React.Component{
    constructor(props){
        super(props);

        this.state={
            postname:"",
            category:"cat",
            file:"",
            url:"./image.jpeg",

            array: [1,2,3,4],
            age:false,

            name:'rsz_4.png'

        }
    }

    handleChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({ [nam]: val });

      };

    handleChangeFile= event =>{
        this.setState({
            file:event.target.files[0]
        })
    }

      handleSubmit = (event) => {


        event.preventDefault();
        this.state.array.push(10);

        this.setState({age:true})
         console.log("this.state", this.state);

         const formData =  new FormData();
         formData.append("image",this.state.file)
         formData.append("category",this.state.category)
         formData.append("postname",this.state.postname)



        //  console.log('form data---',formData);

            const config = {
                headers:{
                    "content-type": "multipart/form-data"
                }
            }

         Axios.post("http://localhost:9000/upload", formData,config).then(function (response) {

           console.log("upload response++++++++",response.data);


         })



      };

    render(){
        return(
            <div>

                <form onSubmit={this.handleSubmit}>

                <div>
                        Post Name:<br/>
                        <input type="text" name="postname" onChange={this.handleChange} required/>                        

                    </div><br/>

                    <div>
                        Category:<br/>

                        <select name="category" onChange={this.handleChange} required>
                            <option value="cat">Cat</option>
                            <option value="dog">Dog</option>
                            <option value="bird">Birds</option>
                            <option value="rabbit">Rabbit</option>
                            <option value="other">Other</option>

                        </select>

                    </div><br/>

                    <div>
                        post:<br/>
                        <input type="file" name="image" onChange={this.handleChangeFile} required/>                        

                    </div><br/>

                    <div>

                        <input type="submit" name="category" value="upload" />                        

                    </div>

                </form>

                <div>
                {this.state.array.map(item => <p>{item}</p>)}

                </div>



                 {/* <div>
                        <img src={require('./posts/rsz_4.png')} alt={"cdjjsdnsd"} style={{height:"300px",width:'500px'}}/>
                    </div>  */}




 {/* {(this.state.age > 5)?(this.state.array.map(item => <h1>{item}</h1>)):""}                    */}


            </div>
        )
    }
}
