import React from 'react';
import Axios from 'axios';
import {Link, Redirect} from 'react-router-dom';
import {
	withRouter
} from 'react-router-dom';

export default class Login extends React.Component{
  constructor(props) {
    super(props);
    console.log("props  are   +++++",props);
    this.state = {
      email: "",
      password: "",
      // check:false
    };

    if(localStorage.length === 0)
    {
      localStorage.clear();
    }
    else
    {
      this.props.history.push("/timeline");
    }
  }



  handleChange = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });

  };

  handleSubmit = (event) => {

    console.log("client side %%%%%");

    event.preventDefault();
    console.log("this.state", this.state);


    Axios.post("http://localhost:9000/login", this.state).then((response) =>{

      console.log("ccccccccccccccccccc",response.data);
      // console.log("user login +++++ client")

      // if(response.data == "exists")
      // {
      //     document.getElementById('mail').innerHTML = "user already exists"
      //     document.getElementById('created').innerHTML = ""

      //  //   document.getElementById('eml').style.backgroundColor = "red"
      // }
      // else
      // {
      //  document.getElementById('mail').innerHTML = ""
      //  document.getElementById('created').innerHTML = "userId created successfully"

      //  document.getElementById('eml').style.backgroundColor = "lightblue"

      // }


      console.log("cbjmdc +++++++++++++++",response.data);

      if(response.data === "incorrect email")
