import React from 'react';
import Axios from 'axios';
import Img from 'react-image';
// const Img = require('react-image');
import Logo from './posts/rsz_4.png'

export default class Upload extends React.Component{
    constructor(props){
        super(props);

        this.state={
            postname:"",
            category:"CATS",
            file:"",
            url:"./image.jpeg",

            array:[],
            // item:""

        }
    }

    handle = (event)=>{


        //  this.setState({item:event.target.value});

        console.log("value is +++++++",event.target.value);
       // console.log("item is +++++",item);


           var item =  this.state.array;
           item.push(event.target.value);

           this.setState({array:item})



        console.log(this.state.array);
    }

    handleChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({ [nam]: val });

      };

    handleChangeFile= event =>{
        this.setState({
            file:event.target.files[0]
        })
    }

      handleSubmit = (event) => {


        event.preventDefault();
        this.state.array.push(10);


         console.log("this.state", this.state);

         const formData =  new FormData();
         formData.append("image",this.state.file)
         formData.append("category",this.state.category)
         formData.append("postname",this.state.postname)



        //  console.log('form data---',formData);

            const config = {
                headers:{
                    "content-type": "multipart/form-data"
                }
            }

         Axios.post("http://localhost:9000/upload", formData,config).then(function (response) {

           console.log("upload response++++++++",response.data);


         })



      };

    render(){
        return(
        <div>
            <h1>Upload post:</h1>
            <form onSubmit={this.handleSubmit}>
                POST NAME:<br/>
                <input type="text" name="postname" onChange={this.handleChange}/><br/><br/>

                CATEGORY:<br/>
                <select name="category" onChange={this.handleChange}>
                    <option value="CATS">CATS</option>
                    <option value="DOGS">DOGS</option>
                    <option value="BIRDS">BIRDS</option>
                    <option value="RABBITS">RABBITS</option>
                    <option value="OTHERS">OTHERS</option>
                </select><br/><br/>

                image:<br/>
                <input type="file" name="file" onChange={this.handleChangeFile}/><br/><br/>

                <input type="submit" value="Post it"/>
            </form>

        </div>  
        )
    }
}

