import React from 'react';
import Axios from 'axios';

export default class Upload extends React.Component{
    constructor(props){
        super(props);

        this.state={
            postname:"",
            category:"cat",
            file:""
        }
    }

    handleChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({ [nam]: val });

      };

    handleChangeFile= event =>{
        console.log("file",event.target)
        this.setState({
            file:event.target.file
        })
    }

      handleSubmit = (event) => {

        console.log("post uploaded +++++++++");

        event.preventDefault();
         console.log("this.state", this.state.file);

         const formdata =  new FormData();
         formdata.append("file",this.state.file)
         formdata.append("postname",this.state.postname)

         Axios.post("http://localhost:9000/upload", formdata).then(function (response) {

           console.log(response.data);


         })

      };

    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                <div>
                        Post Name:<br/>
                        <input type="text" name="postname" onChange={this.handleChange} required/>                        

                    </div><br/>

                    <div>
                        Category:
                        {/* <input type="text" name="category" onChange={this.handleChange} required/>*/}
                        <select name="category" onChange={this.handleChange} required>
                            <option value="cat">Cat</option>
                            <option value="dog">Dog</option>
                            <option value="bird">Birds</option>
                            <option value="rabbit">Rabbit</option>
                            <option value="other">Other</option>

                        </select>

                    </div><br/>

                    <div>
                        post:<br/>
                        <input type="file" name="file" onChange={this.handleChangeFile} required/>                        

                    </div><br/>

                    <div>

                        <input type="submit" name="category" value="upload" />                        

                    </div>

                </form>
            </div>
        )
    }
}
