import React from 'react';
import Child from './child';

export default class Parent extends React.Component{
    constructor(props){
        super(props);

        this.state={
            fname:"mohit",
            lname:"aggarwal"
        }
    }

    handleChange =(event)=> {
        var nam = event.target.name;
        var val = event.target.value;

        this.setState({
            [nam]:val
        })

    }

    render(){
