import React from 'react';

class AddCategory extends React.Component{
    constructor(props){
        super(props);

        this.state={
            category:""

        }
    }

    handleCategory = () =>{

        this.props.AddCat(this.state.category);

    }

    handleChange=(event)=>{
        this.setState({category:event.target.value})
    }

    render(){
        return(
            <div>
                <form onSubmit={this.handleCategory}>
                    <h1>Add Category:</h1><br/>

                    thumbnail:<br/>
                    <input type="file" name="thumbnail" required/><br/><br/>

                    name:<br/>
                    <input type="text" name="category" onChange={this.handleCategory} required/><br/><br/>

                    <input type="submit" value="add to the list"/>
                </form>
            </div>
        )
    }
}

export default AddCategory;
